<?phpnamespace models;require_once '../core/DatabaseManager.php';use DatabaseManager;class User extends DatabaseManager{    private $db;    public function __construct()    {        $this->db = new DatabaseManager();    }    public function createUser($data) //creating user model    {        return $this->db->insert('users',$data);    }   public function showAll_whereStatus_Active($status) // show All users where ststus is Active   {    return $this->db->showAll_whereStatusActive('users','userStatus',$status);   }   public function showBaseOnId($Id)   {        return $this->db->SelectBaseOnId('users',$Id);   }   public function update($table,$data,$condation)   {       return $this->db->update($table,$data,$condation);   }   public function deleteUser($userId)   {    return $this->db->delete('users','id',$userId);   }}