<?phpnamespace models;require_once '../core/DatabaseManager.php';use DatabaseManager;class User extends DatabaseManager{    private $db;    public function __construct()    {        $this->db = new DatabaseManager();    }    public function createUser($data) //creating user model    {        return $this->db->insert('users',$data);    }   public function showAll_whereStatus_Active($status) // show All users where ststus is Active   {    $ActiveUser = $this->db->showAll_whereStatusActive('users','userStatus',$status);    return $ActiveUser;   }   public function showBaseOnId($Id)   {       $user = $this->db->SelectBaseOnId('users',$Id);       return $user;   }}