<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="css/bootstrap.min.css">    <script src="js/bootstrap.min.js"></script>    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>    <title>Crud</title></head><body><section>    <header class="p-3 bg-primary">        <button data-bs-target="#createUser" data-bs-toggle="modal" class="btn btn-outline-light">            Create User        </button>    </header>    <table class="table">        <thead>        <tr>            <th scope="col">#</th>            <th scope="col">Username</th>            <th scope="col">Email</th>            <th scope="col">UserStatus</th>            <th scope="col">Action</th>        </tr>        </thead>        <tbody id="DisplayList">        </tbody>    </table></section><!-- Modal --><form class="modal fade" id="createUser" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered">        <div class="modal-content">            <div class="modal-header">                <h1 class="modal-title fs-5" id="exampleModalLabel">User Details</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <div class="form-floating mb-3">                    <input type="text" name="username" class="form-control" id="username" placeholder="username">                    <label for="username">Username</label>                </div>                <div class="form-floating">                    <input name="userEmail" type="email" class="form-control" id="Email" placeholder="Email">                    <label for="Email">Email</label>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                <button type="submit" class="btn btn-primary">Create</button>            </div>        </div>    </div></form></body></html><script>    $(document).ready(function () {        $('#createUser').submit(function (e) {            e.preventDefault();            const DataForm = new FormData(this);            DataForm.append('action', 'createUser');            $.ajax({                url: 'app/controllers/userController.php',                type: 'POST',                data: DataForm,                dataType: 'json',                processData: false,                contentType: false,                success: function (response) {                    if (response.success) {                        Swal.fire({                            icon: 'success',                            title: 'Success',                            text: response.message,                        });                        $('#createUser').modal('hide');                        $('#createUser').trigger('reset');                        setTimeout(() => {                            location.reload();                        }, 2000);                    } else {                        Swal.fire({                            icon: 'error',                            title: 'Error',                            text: response.message,                        });                    }                }            });        });        // get the user        $.ajax({            url: 'app/controllers/userController.php',            type: 'POST',            data: {action: 'showActiveUser'},            success: function (response) {              $('#DisplayList').html(respon